ruleToc.chapterList:
class.chapter-list.0@children
@js:
var ret = new Array();
var currentVolume = '';
for (var i = 0; i < result.length; i++) {
    var currentElement = result[i];
    if (currentElement.tagName().toLowerCase() == 'div') {
        currentVolume = currentElement.text();
    }
    else {
        currentElement.children()[0].text(currentVolume + ' ' + currentElement.children()[0].text());
        ret.push(currentElement);
    }
}
ret
