ruleContent.content:
$.data.pageInfos.length()
@js:
let page_url = baseUrl + '/page?pageNum=';
let img_content = '<img src="{url}">';
let ret = [];
java.ajaxAll(Array.from({ length: parseInt(result) }, (v, i) => page_url + (i + 1))).forEach((resp, index, array) => {
    let json = JSON.parse(resp.body());
    ret[json.data.pageNum] = img_content.replace('{url}', json.data.url);
});
ret.join('\n')

